def PA_SCHEDULER_REST_URL = variables.get('PA_SCHEDULER_REST_URL') // 'http://10.19.197.100:8080/rest'
def hostIp = new URL(PA_SCHEDULER_REST_URL).getHost()
def hostPort = new File('host_port').text

variables.put("endpoint", hostIp + ":" + hostPort)
